import React from 'react';
import '../styles/CollectionTable.css';

const CollectionTable = ({ collections, isLoading, error }) => {
  const handleCollectionClick = (collectionName) => {
    // Construct the API URL
    const apiUrl = `/api/v1/collections/${collectionName}`;

    // For now, let's log the URL and fetch the data
    console.log('Fetching collection data:', apiUrl);
    // TODO: Implement data fetching and display
  };

  if (isLoading) return <div>Loading collections...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!collections?.length) return <div>No collections found</div>;

  return (
    <div className="collection-table-container">
      <table className="collection-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Status</th>
            <th>Count</th>
          </tr>
        </thead>
        <tbody>
          {collections.map((collection) => (
            <tr key={collection.name}>
              <td>
                <a 
                  href="#"
                  className="collection-link"
                  onClick={(e) => {
                    e.preventDefault();
                    handleCollectionClick(collection.name);
                  }}
                >
                  {collection.name}
                </a>
              </td>
              <td>{collection.type}</td>
              <td>{collection.status}</td>
              <td>{collection.count}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CollectionTable;
