import React, { useState } from 'react';
import NetworkGraph from './components/NetworkGraph';
import Sidebar from './components/Sidebar';
import './App.css';
import './NetworkGraph.css';

function App() {
  const [selectedCollection, setSelectedCollection] = useState(null);

  const handleCollectionSelect = (collection) => {
    setSelectedCollection(collection);
    // We'll update NetworkGraph to use this selection
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-content">
          <img 
            src={process.env.PUBLIC_URL + '/images/jalapeno-icon.png'} 
            alt="Jalapeno Logo" 
            className="logo"
          />
          <h1>Jalapeno Network Topology</h1>
        </div>
      </header>
      <div className="main-container">
        <Sidebar onCollectionSelect={handleCollectionSelect} />
        <div className="content">
          <NetworkGraph collection={selectedCollection} />
        </div>
      </div>
    </div>
  );
}

export default App;
