import React, { useEffect, useRef, useState } from 'react';
import CytoscapeComponent from 'react-cytoscapejs';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import { apiUrl } from '../config';
import '../styles/NetworkGraph.css';

const COLORS = {
  node: '#CC4A04',     // Default node color (Cayenne orange)
  prefix: '#002921',   // Color for prefixes
  host: '#1E88E5',     // Blue for future host nodes
  service: '#7CB342',  // Green for future service nodes
  gpu: '#6D4C41',      // Brown for future GPU nodes
  text: '#000',        // Black
  edge: '#1a365d'      // Blue
};

cytoscape.use(cola);

const Legend = () => (
  <div className="graph-legend">
    <h3>Legend</h3>
    <div className="legend-item">
      <span className="legend-color" style={{ backgroundColor: COLORS.node }}></span>
      <span>Nodes</span>
    </div>
    <div className="legend-item">
      <span className="legend-color" style={{ backgroundColor: COLORS.prefix }}></span>
      <span>Prefixes</span>
    </div>
  </div>
);

const NetworkGraph = ({ collection }) => {
  const cyRef = useRef(null);
  const [graphData, setGraphData] = useState(null);
  const [verticesData, setVerticesData] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadGraphData = async () => {
      if (!collection) return;
      setIsLoading(true);
      setError(null);

      try {
        const response = await fetch(`${apiUrl}/api/v1/collections/${collection}/topology`);
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();
        console.log('Topology data:', data); // Debug log
        const elements = transformDataToCytoscape(data);
        setGraphData(elements);
      } catch (err) {
        console.error('Detailed error:', err);
        setError(`Failed to load graph data: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadGraphData();
  }, [collection]);

  const transformDataToCytoscape = (data) => {
    console.log('Raw data:', {
      edgeCount: data.edges.length,
      vertexCount: Object.keys(data.vertices).length,
      vertices: data.vertices,
      edges: data.edges
    });

    const elements = {
      nodes: new Set(),
      edges: new Set()
    };

    if (!data?.edges || !data?.vertices) {
      console.warn('Missing edges or vertices in data');
      return [];
    }

    // First, add all vertices as nodes
    Object.entries(data.vertices).forEach(([id, vertex]) => {
      elements.nodes.add({
        group: 'nodes',
        data: {
          id: id,
          label: vertex.name || id.split('/')[1],
          type: id.split('/')[0],
          vertexData: vertex
        }
      });
    });

    // Then process edges (deduplicating as we go)
    const processedEdgePairs = new Set();

    data.edges.forEach(edge => {
      if (edge._from && edge._to) {
        // Create a consistent edge identifier by sorting vertex IDs
        const vertices = [edge._from, edge._to].sort();
        const edgeKey = vertices.join('_');

        if (!processedEdgePairs.has(edgeKey)) {
          processedEdgePairs.add(edgeKey);

          elements.edges.add({
            group: 'edges',
            data: {
              id: edgeKey,
              source: vertices[0],
              target: vertices[1]
            }
          });
        }
      }
    });

    const finalElements = [...Array.from(elements.nodes), ...Array.from(elements.edges)];

    console.log('Transformed data:', {
      totalElements: finalElements.length,
      nodes: Array.from(elements.nodes).length,
      edges: Array.from(elements.edges).length,
      nodeTypes: Array.from(elements.nodes).reduce((acc, node) => {
        const type = node.data.type;
        acc[type] = (acc[type] || 0) + 1;
        return acc;
      }, {})
    });

    return finalElements;
  };

  const getTooltipContent = (node) => {
    const vertexData = node.data('vertexData') || {};
    const type = node.data('type');

    let content = `<div class="tooltip-header">${type}</div><div class="tooltip-body">`;

    // Add common fields if they exist
    if (vertexData.name) {
      content += `<div>Name: ${vertexData.name}</div>`;
    }
    if (vertexData.asn) {
      content += `<div>ASN: ${vertexData.asn}</div>`;
    }

    // Add type-specific fields
    switch (type) {
      case 'igp_node':
        if (vertexData.protocol) {
          content += `<div>Protocol: ${vertexData.protocol}</div>`;
        }
        if (vertexData.sids && vertexData.sids.length > 0) {
          content += `<div>SIDs: ${vertexData.sids.join(', ')}</div>`;
        }
        break;

      case 'ls_prefix':
        if (vertexData.prefix) {
          content += `<div>Prefix: ${vertexData.prefix}</div>`;
        }
        break;

      case 'bgp_node':
        if (vertexData.peer_ip) {
          content += `<div>Peer IP: ${vertexData.peer_ip}</div>`;
        }
        if (vertexData.address_family) {
          content += `<div>Address Family: ${vertexData.address_family}</div>`;
        }
        break;
    }

    content += '</div>';
    return content;
  };

  // Setup hover events after the graph is rendered
  useEffect(() => {
    if (cyRef.current) {
      const cy = cyRef.current;

      // Remove any existing event listeners
      cy.nodes().unbind('mouseover mouseout');

      // Add hover events
      cy.nodes().bind('mouseover', (event) => {
        const node = event.target;
        const type = node.data('type');
        const label = node.data('label');

        // Create and position the tooltip div
        let tooltip = document.getElementById('cy-tooltip');
        if (!tooltip) {
          tooltip = document.createElement('div');
          tooltip.setAttribute('id', 'cy-tooltip');
          document.body.appendChild(tooltip);
        }

        tooltip.innerHTML = `
          <div class="tooltip-header">${type}</div>
          <div class="tooltip-body">
            <div>ID: ${label}</div>
          </div>
        `;

        const renderedPosition = node.renderedPosition();
        const zoom = cy.zoom();

        tooltip.style.display = 'block';
        tooltip.style.left = `${renderedPosition.x}px`;
        tooltip.style.top = `${renderedPosition.y - 70}px`; // Offset above the node
      });

      cy.nodes().bind('mouseout', () => {
        const tooltip = document.getElementById('cy-tooltip');
        if (tooltip) {
          tooltip.style.display = 'none';
        }
      });
    }
  }, [graphData]);

  const detectTopology = (elements) => {
    if (!elements || elements.length === 0) {
      return { isClos: false, reason: 'No elements to analyze' };
    }

    const nodes = elements.filter(ele => ele.group === 'nodes');
    const edges = elements.filter(ele => ele.group === 'edges');

    // Build adjacency map for IGP nodes only
    const igpAdjacency = {};
    edges.forEach(edge => {
      const sourceNode = nodes.find(n => n.data.id === edge.data.source);
      const targetNode = nodes.find(n => n.data.id === edge.data.target);

      // Only consider edges between IGP nodes
      if (sourceNode?.data.type === 'igp_node' && targetNode?.data.type === 'igp_node') {
        if (!igpAdjacency[edge.data.source]) igpAdjacency[edge.data.source] = new Set();
        if (!igpAdjacency[edge.data.target]) igpAdjacency[edge.data.target] = new Set();
        igpAdjacency[edge.data.source].add(edge.data.target);
        igpAdjacency[edge.data.target].add(edge.data.source);
      }
    });

    // Get IGP nodes and their degrees
    const igpNodes = nodes.filter(node => node.data.type === 'igp_node');
    console.log('IGP nodes found:', igpNodes.length);

    // Calculate and analyze IGP node degrees
    const nodeDegrees = {};
    igpNodes.forEach(node => {
      nodeDegrees[node.data.id] = igpAdjacency[node.data.id]?.size || 0;
    });

    const degrees = Object.values(nodeDegrees);
    const sortedDegrees = [...degrees].sort((a, b) => b - a);

    console.log('Degree distribution:', {
      degrees: sortedDegrees,
      nodeCount: igpNodes.length,
      uniqueDegrees: [...new Set(sortedDegrees)]
    });

    // CLOS criteria adjusted for your topology
    const uniqueDegrees = new Set(sortedDegrees);
    const hasExactlyTwoDegrees = uniqueDegrees.size === 2;
    const degreesAreEven = [...uniqueDegrees].every(d => d % 2 === 0);
    const hasRegularPattern = hasExactlyTwoDegrees && degreesAreEven;

    console.log('Refined CLOS analysis:', {
      uniqueDegrees: [...uniqueDegrees],
      hasExactlyTwoDegrees,
      degreesAreEven,
      hasRegularPattern,
      higherDegreeCount: sortedDegrees.filter(d => d === Math.max(...sortedDegrees)).length,
      lowerDegreeCount: sortedDegrees.filter(d => d === Math.min(...sortedDegrees)).length
    });

    const isClos = hasRegularPattern;

    return {
      isClos,
      reason: isClos ? 'Regular CLOS pattern detected' : 'Node degree distribution does not match CLOS pattern',
      analysis: {
        hasRegularPattern,
        spineCount: sortedDegrees.filter(d => d === Math.max(...sortedDegrees)).length,
        leafCount: sortedDegrees.filter(d => d === Math.min(...sortedDegrees)).length,
        spineNodes: igpNodes
          .filter(n => nodeDegrees[n.data.id] === Math.max(...sortedDegrees))
          .map(n => n.data.id)
      }
    };
  };

  const getLayout = (elements) => {
    const topology = detectTopology(elements);
    console.log('Topology detection result:', topology);

    if (topology.isClos) {
      console.log('Applying CLOS layout');
      return {
        name: 'preset',
        positions: (node) => {
          const width = cyRef.current?.width() || 1000;
          const height = cyRef.current?.height() || 800;
          const type = node.data('type');

          console.log('Positioning node:', {
            id: node.data('id'),
            type: type,
            width: width,
            height: height
          });

          // Group nodes by type for counting
          const nodesByType = {
            spine: nodes.filter(n => n.data.type === 'igp_node' && n.data.vertexData?.role === 'spine'),
            leaf: nodes.filter(n => n.data.type === 'igp_node' && n.data.vertexData?.role !== 'spine'),
            bgp: nodes.filter(n => n.data.type === 'bgp_node'),
            prefix: nodes.filter(n => n.data.type === 'ls_prefix')
          };

          // Calculate position based on node type
          if (type === 'igp_node') {
            const isSpine = node.data('vertexData')?.role === 'spine';
            const typeNodes = isSpine ? nodesByType.spine : nodesByType.leaf;
            const index = typeNodes.indexOf(node);
            const total = typeNodes.length;

            return {
              x: width * (index + 1) / (total + 1),
              y: isSpine ? height * 0.2 : height * 0.5
            };
          } else if (type === 'bgp_node') {
            const index = nodesByType.bgp.indexOf(node);
            const total = nodesByType.bgp.length;
            return {
              x: width * (index + 1) / (total + 1),
              y: height * 0.35
            };
          } else if (type === 'ls_prefix') {
            const index = nodesByType.prefix.indexOf(node);
            const total = nodesByType.prefix.length;
            return {
              x: width * (index + 1) / (total + 1),
              y: height * 0.8
            };
          }

          return { x: width / 2, y: height / 2 };
        },
        fit: true,
        padding: 50
      };
    } else {
      console.log('Applying force-directed layout');
      return {
        name: 'cola',
        animate: true,
        refresh: 1,
        maxSimulationTime: 3000,
        nodeSpacing: 100,
        edgeLength: 200,
        randomize: true,
        infinite: true
      };
    }
  };

  // Ensure layout is applied after graph data changes
  useEffect(() => {
    if (graphData && cyRef.current) {
      console.log('Applying layout to graph');
      const layout = getLayout(graphData);
      if (layout) {
        cyRef.current.layout(layout).run();
      }
    }
  }, [graphData]);

  const stylesheet = [
    {
      selector: 'node',
      style: {
        'background-color': (ele) => {
          const nodeType = ele.data('type');
          switch (nodeType) {
            case 'igp_node':
              return COLORS.node;
            case 'bgp_node':
              return COLORS.host;
            case 'ls_prefix':
              return COLORS.prefix;
            default:
              return COLORS.node;
          }
        },
        'label': 'data(label)',
        'width': 30,
        'height': 30,
        'color': COLORS.text,
        'text-outline-width': 2,
        'text-outline-color': '#fff',
        'font-family': 'Consolas',
        'font-size': '10px',
        'text-wrap': 'wrap',
        'text-max-width': '100px'
      }
    },
    {
      selector: 'edge',
      style: {
        'width': 1.5,
        'line-color': COLORS.edge,
        'curve-style': 'bezier',
        'target-arrow-color': COLORS.edge,
        'target-arrow-shape': 'triangle',
        'arrow-scale': 0.8
      }
    }
  ];

  if (isLoading) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Loading graph data...</div>;
  }

  if (error) {
    return <div style={{ padding: '20px', color: 'red', fontFamily: 'Consolas' }}>{error}</div>;
  }

  if (!graphData) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Select a graph to visualize</div>;
  }

  return (
    <div className="network-graph-container">
      <Legend />
      <div className="graph-area">
        <CytoscapeComponent
          elements={graphData}
          layout={getLayout(graphData)}
          stylesheet={stylesheet}
          cy={(cy) => { cyRef.current = cy; }}
          style={{ width: '100%', height: '100%' }}
        />
      </div>
    </div>
  );
};

export default NetworkGraph;
