import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000'; // Adjust to match your API URL

export const fetchTopology = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/topology`);
    return response.data;
  } catch (error) {
    console.error('Error fetching topology:', error);
    throw error;
  }
};

export const calculatePath = async (sourceId, targetId) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/path`, {
      params: { source: sourceId, target: targetId }
    });
    return response.data;
  } catch (error) {
    console.error('Error calculating path:', error);
    throw error;
  }
};
