import React, { useState, useEffect } from 'react';
import '../styles/Sidebar.css';
import { fetchCollections } from '../services/api';

const Sidebar = () => {
  const [collections, setCollections] = useState([]);
  const [graphs, setGraphs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // TODO: Replace with actual API calls
    setGraphs(['LSP Graph', 'IGP Graph', 'BGP Graph']);
    setCollections(['Nodes', 'Links', 'Prefixes', 'LSPs']);
    setIsLoading(false);
  }, []);

  return (
    <div className="sidebar">
      <div className="sidebar-section">
        <h3>Select Graph</h3>
        <select 
          className="sidebar-dropdown"
          onChange={(e) => console.log('Selected graph:', e.target.value)}
        >
          <option value="">Select a graph...</option>
          {graphs.map((graph, index) => (
            <option key={index} value={graph}>{graph}</option>
          ))}
        </select>
      </div>

      <div className="sidebar-section">
        <h3>Explore Network</h3>
        <select 
          className="sidebar-dropdown"
          onChange={(e) => console.log('Selected collection:', e.target.value)}
        >
          <option value="">Select a collection...</option>
          {collections.map((collection, index) => (
            <option key={index} value={collection}>{collection}</option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Sidebar;
