import React, { useEffect, useRef, useState } from 'react';
import CytoscapeComponent from 'react-cytoscapejs';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import { apiUrl } from '../config';
import '../styles/NetworkGraph.css';

const COLORS = {
  igp_node: '#CC4A04',    // Cayenne orange for IGP nodes
  bgp_node: '#1E88E5',    // Blue for BGP nodes
  prefix: '#002921',      // Dark green for all prefix types
  text: '#000',           // Black text
  edge: '#1a365d'         // Blue edges
};

cytoscape.use(cola);

const NetworkGraph = ({ collection }) => {
  const cyRef = useRef(null);
  const [graphData, setGraphData] = useState(null);

  const isPrefix = (type) => {
    return type === 'ls_prefix' || type === 'bgp_prefix';
  };

  // Legend component definition
  const Legend = () => (
    <div className="graph-legend" style={{
      position: 'absolute',
      top: '10px',
      right: '10px',
      background: 'white',
      padding: '10px',
      borderRadius: '5px',
      boxShadow: '0 0 10px rgba(0,0,0,0.1)'
    }}>
      <h3 style={{ margin: '0 0 10px 0' }}>Legend</h3>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
          <span style={{ 
            width: '20px', 
            height: '20px', 
            backgroundColor: COLORS.igp_node,
            display: 'inline-block',
            borderRadius: '3px'
          }}></span>
          <span>IGP Nodes</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
          <span style={{ 
            width: '20px', 
            height: '20px', 
            backgroundColor: COLORS.bgp_node,
            display: 'inline-block',
            borderRadius: '3px'
          }}></span>
          <span>BGP Nodes</span>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
          <span style={{ 
            width: '20px', 
            height: '20px', 
            backgroundColor: COLORS.prefix,
            display: 'inline-block',
            borderRadius: '3px'
          }}></span>
          <span>Prefixes</span>
        </div>
      </div>
    </div>
  );

  const transformDataToCytoscape = (data) => {
    if (!data?.edges || !data?.vertices) {
      console.warn('Missing edges or vertices in data');
      return [];
    }

    const elements = [];

    // Add vertices as nodes
    Object.entries(data.vertices).forEach(([id, vertex]) => {
      const nodeType = id.split('/')[0]; // e.g., 'igp_node', 'ls_prefix', 'bgp_node'
      elements.push({
        group: 'nodes',
        data: {
          id: id,
          label: vertex.name || id.split('/')[1],
          type: nodeType,
          vertexData: vertex
        }
      });
    });

    // Add edges
    const processedEdges = new Set();
    data.edges.forEach(edge => {
      if (edge._from && edge._to) {
        const edgeId = `${edge._from}-${edge._to}`;
        if (!processedEdges.has(edgeId)) {
          processedEdges.add(edgeId);
          elements.push({
            group: 'edges',
            data: {
              id: edgeId,
              source: edge._from,
              target: edge._to
            }
          });
        }
      }
    });

    console.log('Transformed elements:', {
      total: elements.length,
      nodes: elements.filter(e => e.group === 'nodes').length,
      edges: elements.filter(e => e.group === 'edges').length
    });

    return elements;
  };

  useEffect(() => {
    console.log('Collection received:', {
      collection,
      type: typeof collection,
      keys: collection ? Object.keys(collection) : 'none'
    });

    // If collection is just the name, we need to fetch the data
    if (typeof collection === 'string') {
      // Make sure we're using the environment variable
      const baseUrl = process.env.REACT_APP_API_URL;
      if (!baseUrl) {
        console.error('REACT_APP_API_URL is not defined in environment variables');
        return;
      }

      // Remove any trailing slash from baseUrl
      const cleanBaseUrl = baseUrl.replace(/\/$/, '');
      const endpoint = `${cleanBaseUrl}/api/v1/collections/${collection}/topology`;

      console.log('Fetching topology from:', {
        baseUrl: cleanBaseUrl,
        endpoint: endpoint,
        env: process.env.REACT_APP_API_URL
      });

      fetch(endpoint)
        .then(async response => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          console.log('Fetched topology data:', {
            vertices: Object.keys(data.vertices).length,
            edges: data.edges.length
          });
          const elements = transformDataToCytoscape(data);
          setGraphData(elements);
        })
        .catch(error => {
          console.error('Error fetching topology:', {
            message: error.message,
            endpoint,
            baseUrl: cleanBaseUrl
          });
        });
    }
  }, [collection]);

  useEffect(() => {
    console.log('Graph data changed:', graphData);
    if (cyRef.current && graphData) {
      const cy = cyRef.current;
      console.log('Applying layout to', cy.elements().length, 'elements');

      // Run layouts sequentially
      const nonPrefixNodes = cy.nodes().filter(node => !isPrefix(node.data('type')));
      const prefixNodes = cy.nodes().filter(node => isPrefix(node.data('type')));

      console.log('Node counts:', {
        total: cy.nodes().length,
        nonPrefix: nonPrefixNodes.length,
        prefix: prefixNodes.length
      });

      // Position non-prefix nodes in a circle
      nonPrefixNodes.layout({
        name: 'circle',
        animate: false,
        padding: 50,
        radius: 150,
        startAngle: 3/2 * Math.PI,
        sweep: 2 * Math.PI,
        clockwise: true
      }).run();

      // Position prefix nodes in a line
      const width = cy.width();
      const height = cy.height();

      prefixNodes.forEach((node, index) => {
        const totalPrefixes = prefixNodes.length;
        const prefixWidth = width * 0.8;
        const startX = width * 0.1;
        const x = startX + (prefixWidth * index / (totalPrefixes - 1 || 1));

        node.position({
          x: x,
          y: height * 0.8
        });
      });

      // Fit the viewport
      cy.fit(undefined, 50);
    }
  }, [graphData]);

  const stylesheet = [
    {
      selector: 'node',
      style: {
        'background-color': (ele) => {
          const type = ele.data('type');
          if (isPrefix(type)) return COLORS.prefix;
          return COLORS[type] || COLORS.igp_node;
        },
        'label': 'data(label)',
        'width': 30,
        'height': 30,
        'color': COLORS.text,
        'text-outline-width': 2,
        'text-outline-color': '#fff',
        'font-family': 'Consolas',
        'font-size': '10px',
        'text-wrap': 'wrap',
        'text-max-width': '100px'
      }
    },
    {
      selector: 'edge',
      style: {
        'width': 1.5,
        'line-color': COLORS.edge,
        'curve-style': 'bezier',
        'target-arrow-color': COLORS.edge,
        'target-arrow-shape': 'triangle',
        'arrow-scale': 0.8
      }
    }
  ];

  return (
    <div className="network-graph" style={{ width: '100%', height: '800px', position: 'relative' }}>
      <CytoscapeComponent
        cy={(cy) => { 
          console.log('Cytoscape initialized');
          cyRef.current = cy;
        }}
        elements={graphData || []}
        style={{ width: '100%', height: '100%' }}
        stylesheet={stylesheet}
        userZoomingEnabled={true}
        userPanningEnabled={true}
      />
      <Legend />
    </div>
  );
};

export default NetworkGraph;
