import React, { useState, useEffect } from 'react';
import '../styles/Sidebar.css';
import { fetchGraphs, fetchCollections } from '../services/api';

const Sidebar = ({ onGraphSelect }) => {
  const [graphs, setGraphs] = useState([]);
  const [collections, setCollections] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        const [graphsData, collectionsData] = await Promise.all([
          fetchGraphs(),
          fetchCollections()
        ]);
        setGraphs(graphsData);
        setCollections(collectionsData);
        setError(null);
      } catch (err) {
        setError('Failed to load data');
        console.error('Error loading data:', err);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  return (
    <div className="sidebar">
      <div className="sidebar-section">
        <h3>Select Graph</h3>
        {isLoading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          <select 
            className="sidebar-dropdown"
            onChange={(e) => onGraphSelect(e.target.value)}
          >
            <option value="">Select a graph...</option>
            {graphs.map((graph, index) => (
              <option key={index} value={graph}>{graph}</option>
            ))}
          </select>
        )}
      </div>

      <div className="sidebar-section">
        <h3>Explore Network</h3>
        {isLoading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          <select 
            className="sidebar-dropdown"
            onChange={(e) => console.log('Selected collection:', e.target.value)}
          >
            <option value="">Select a collection...</option>
            {collections.map((collection, index) => (
              <option key={index} value={collection}>{collection}</option>
            ))}
          </select>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
