import React, { useState, useEffect } from 'react';
import '../styles/Sidebar.css';
import { fetchCollections, fetchCollectionInfo } from '../services/api';

const Sidebar = ({ onCollectionSelect }) => {
  const [collections, setCollections] = useState([]);
  const [selectedCollection, setSelectedCollection] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadCollections = async () => {
      try {
        setIsLoading(true);
        const response = await fetchCollections(true);
        console.log('API Response:', response); // Debug log

        // Ensure we have an array of collections
        const collectionsArray = Array.isArray(response) ? response : 
                               Array.isArray(response.data) ? response.data :
                               Object.keys(response || {});

        setCollections(collectionsArray);
        setError(null);
      } catch (err) {
        setError('Failed to load collections');
        console.error('Error loading collections:', err);
      } finally {
        setIsLoading(false);
      }
    };

    loadCollections();
  }, []);

  const handleCollectionSelect = async (collectionName) => {
    if (collectionName) {
      setSelectedCollection(collectionName);
      onCollectionSelect?.(collectionName);
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-section">
        <h3>Select Collection</h3>
        {isLoading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : collections.length > 0 ? (
          <select 
            className="sidebar-dropdown"
            value={selectedCollection}
            onChange={(e) => handleCollectionSelect(e.target.value)}
          >
            <option value="">Select a collection...</option>
            {collections.map((collection, index) => (
              <option key={index} value={collection}>
                {collection}
              </option>
            ))}
          </select>
        ) : (
          <p>No collections available</p>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
