import React, { useEffect, useRef, useState } from 'react';
import CytoscapeComponent from 'react-cytoscapejs';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import { apiUrl } from '../config';
import '../styles/NetworkGraph.css';

const COLORS = {
  node: '#CC4A04',     // Default node color (Cayenne orange)
  prefix: '#002921',   // Color for prefixes
  host: '#1E88E5',     // Blue for future host nodes
  service: '#7CB342',  // Green for future service nodes
  gpu: '#6D4C41',      // Brown for future GPU nodes
  text: '#000',        // Black
  edge: '#1a365d'      // Blue
};

cytoscape.use(cola);

const Legend = () => (
  <div className="graph-legend">
    <h3>Legend</h3>
    <div className="legend-item">
      <span className="legend-color" style={{ backgroundColor: COLORS.node }}></span>
      <span>Nodes</span>
    </div>
    <div className="legend-item">
      <span className="legend-color" style={{ backgroundColor: COLORS.prefix }}></span>
      <span>Prefixes</span>
    </div>
  </div>
);

const NetworkGraph = ({ collection }) => {
  const cyRef = useRef(null);
  const [graphData, setGraphData] = useState(null);
  const [verticesData, setVerticesData] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadGraphData = async () => {
      if (!collection) return;
      setIsLoading(true);
      setError(null);

      try {
        const response = await fetch(`${apiUrl}/api/v1/collections/${collection}/topology`);
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();
        console.log('Topology data:', data); // Debug log
        const elements = transformDataToCytoscape(data);
        setGraphData(elements);
      } catch (err) {
        console.error('Detailed error:', err);
        setError(`Failed to load graph data: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadGraphData();
  }, [collection]);

  const transformDataToCytoscape = (data) => {
    const elements = {
      nodes: new Set(),
      edges: new Set()
    };

    if (!data?.edges || !data?.vertices) {
      console.warn('Missing edges or vertices in data');
      return [];
    }

    // Store vertices data for tooltips
    setVerticesData(data.vertices);

    // Create a Set to track processed edge pairs
    const processedEdges = new Set();

    // Process edges
    Object.entries(data.edges).forEach(([_, edge]) => {
      if (edge._from && edge._to) {
        // Create a consistent edge identifier by sorting the vertex IDs
        const vertices = [edge._from, edge._to].sort();
        const edgeKey = vertices.join('_');

        // Skip if we've already processed this edge pair
        if (processedEdges.has(edgeKey)) {
          return;
        }
        processedEdges.add(edgeKey);

        const fromVertex = data.vertices[edge._from] || {};
        const toVertex = data.vertices[edge._to] || {};

        // Add nodes with their vertex data
        elements.nodes.add({
          data: {
            id: edge._from,
            label: fromVertex.name || edge._from.split('/')[1],
            type: edge._from.split('/')[0],
            vertexData: fromVertex
          }
        });

        elements.nodes.add({
          data: {
            id: edge._to,
            label: toVertex.name || edge._to.split('/')[1],
            type: edge._to.split('/')[0],
            vertexData: toVertex
          }
        });

        // Add single edge
        elements.edges.add({
          data: {
            id: edgeKey,
            source: vertices[0], // Use sorted vertices for consistent direction
            target: vertices[1]
          }
        });
      }
    });

    return [...Array.from(elements.nodes), ...Array.from(elements.edges)];
  };

  const getTooltipContent = (node) => {
    const vertexData = node.data('vertexData') || {};
    const type = node.data('type');

    let content = `<div class="tooltip-header">${type}</div><div class="tooltip-body">`;

    // Add common fields if they exist
    if (vertexData.name) {
      content += `<div>Name: ${vertexData.name}</div>`;
    }
    if (vertexData.asn) {
      content += `<div>ASN: ${vertexData.asn}</div>`;
    }

    // Add type-specific fields
    switch (type) {
      case 'igp_node':
        if (vertexData.protocol) {
          content += `<div>Protocol: ${vertexData.protocol}</div>`;
        }
        if (vertexData.sids && vertexData.sids.length > 0) {
          content += `<div>SIDs: ${vertexData.sids.join(', ')}</div>`;
        }
        break;

      case 'ls_prefix':
        if (vertexData.prefix) {
          content += `<div>Prefix: ${vertexData.prefix}</div>`;
        }
        break;

      case 'bgp_node':
        if (vertexData.peer_ip) {
          content += `<div>Peer IP: ${vertexData.peer_ip}</div>`;
        }
        if (vertexData.address_family) {
          content += `<div>Address Family: ${vertexData.address_family}</div>`;
        }
        break;
    }

    content += '</div>';
    return content;
  };

  // Setup hover events after the graph is rendered
  useEffect(() => {
    if (cyRef.current) {
      const cy = cyRef.current;

      // Remove any existing event listeners
      cy.nodes().unbind('mouseover mouseout');

      // Add hover events
      cy.nodes().bind('mouseover', (event) => {
        const node = event.target;
        const type = node.data('type');
        const label = node.data('label');

        // Create and position the tooltip div
        let tooltip = document.getElementById('cy-tooltip');
        if (!tooltip) {
          tooltip = document.createElement('div');
          tooltip.setAttribute('id', 'cy-tooltip');
          document.body.appendChild(tooltip);
        }

        tooltip.innerHTML = `
          <div class="tooltip-header">${type}</div>
          <div class="tooltip-body">
            <div>ID: ${label}</div>
          </div>
        `;

        const renderedPosition = node.renderedPosition();
        const zoom = cy.zoom();

        tooltip.style.display = 'block';
        tooltip.style.left = `${renderedPosition.x}px`;
        tooltip.style.top = `${renderedPosition.y - 70}px`; // Offset above the node
      });

      cy.nodes().bind('mouseout', () => {
        const tooltip = document.getElementById('cy-tooltip');
        if (tooltip) {
          tooltip.style.display = 'none';
        }
      });
    }
  }, [graphData]);

  const layout = {
    name: 'cola',
    animate: true,
    refresh: 1,
    maxSimulationTime: 3000,
    nodeSpacing: 100,
    edgeLength: 200,
    randomize: true,
    infinite: true
  };

  const stylesheet = [
    {
      selector: 'node',
      style: {
        'background-color': (ele) => {
          const nodeType = ele.data('type');
          switch (nodeType) {
            case 'igp_node':
              return COLORS.node;
            case 'bgp_node':
              return COLORS.host;
            case 'ls_prefix':
              return COLORS.prefix;
            default:
              return COLORS.node;
          }
        },
        'label': 'data(label)',
        'width': 30,
        'height': 30,
        'color': COLORS.text,
        'text-outline-width': 2,
        'text-outline-color': '#fff',
        'font-family': 'Consolas',
        'font-size': '10px',
        'text-wrap': 'wrap',
        'text-max-width': '100px'
      }
    },
    {
      selector: 'edge',
      style: {
        'width': 1.5,
        'line-color': COLORS.edge,
        'curve-style': 'bezier',
        'target-arrow-color': COLORS.edge,
        'target-arrow-shape': 'triangle',
        'arrow-scale': 0.8
      }
    }
  ];

  if (isLoading) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Loading graph data...</div>;
  }

  if (error) {
    return <div style={{ padding: '20px', color: 'red', fontFamily: 'Consolas' }}>{error}</div>;
  }

  if (!graphData) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Select a graph to visualize</div>;
  }

  return (
    <div className="network-graph-container">
      <Legend />
      <div className="graph-area">
        <CytoscapeComponent
          elements={graphData}
          layout={layout}
          stylesheet={stylesheet}
          cy={(cy) => { cyRef.current = cy; }}
          style={{ width: '100%', height: '100%' }}
        />
      </div>
    </div>
  );
};

export default NetworkGraph;
