import React, { useEffect, useRef, useState } from 'react';
import CytoscapeComponent from 'react-cytoscapejs';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import { apiUrl } from '../config';

// Define colors as constants
const COLORS = {
  node: '#CC4A04',    // Cayenne orange
  text: '#000',       // Black
  edge: '#1a365d'     // Blue
};

// Register the cola layout
cytoscape.use(cola);

const NetworkGraph = ({ collection }) => {
  const cyRef = useRef(null);
  const [graphData, setGraphData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadGraphData = async () => {
      if (!collection) return;

      setIsLoading(true);
      setError(null);

      try {
        console.log('Fetching from:', `${apiUrl}/api/v1/collections/${collection}/edges`);
        const response = await fetch(`${apiUrl}/api/v1/collections/${collection}/edges`);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Received data:', data);

        const elements = transformDataToCytoscape(data);
        setGraphData(elements);
      } catch (err) {
        console.error('Detailed error:', err);
        setError(`Failed to load graph data: ${err.message}`);
      } finally {
        setIsLoading(false);
      }
    };

    loadGraphData();
  }, [collection]);

  const transformDataToCytoscape = (data) => {
    console.log('Starting data transformation with:', data);

    const elements = {
      nodes: new Set(),
      edges: []
    };

    // Check the data structure
    if (!data) {
      console.error('No data received');
      return [];
    }

    // Handle if data is wrapped in a response object
    const edgesData = Array.isArray(data) ? data : data.edges || data.data || [];
    console.log('Processing edges data:', edgesData);

    if (edgesData.length === 0) {
      console.warn('No edges found in data');
      return [];
    }

    edgesData.forEach(item => {
      if (item._from && item._to) {
        console.log('Processing edge:', item);

        // Add nodes
        elements.nodes.add({
          data: {
            id: item._from,
            label: item._from.split('/')[1],
            type: item._from.split('/')[0]
          }
        });

        elements.nodes.add({
          data: {
            id: item._to,
            label: item._to.split('/')[1],
            type: item._to.split('/')[0]
          }
        });

        // Add edge
        elements.edges.push({
          data: {
            id: `${item._from}-${item._to}`,
            source: item._from,
            target: item._to
          }
        });
      } else {
        console.warn('Invalid edge data:', item);
      }
    });

    const finalElements = [...Array.from(elements.nodes), ...elements.edges];
    console.log('Final transformed elements:', finalElements);
    return finalElements;
  };

  const layout = {
    name: 'cola',
    animate: true,
    refresh: 1,
    maxSimulationTime: 3000,
    nodeSpacing: 100,
    edgeLength: 200,
    randomize: true,
    infinite: true
  };

  const stylesheet = [
    {
      selector: 'node',
      style: {
        'background-color': COLORS.node,
        'label': 'data(label)',
        'width': 30,
        'height': 30,
        'color': COLORS.text,
        'text-outline-width': 2,
        'text-outline-color': '#fff',
        'font-family': 'Consolas',
        'font-size': '10px',
        'text-wrap': 'wrap',
        'text-max-width': '100px'
      }
    },
    {
      selector: 'edge',
      style: {
        'width': 1.5,
        'line-color': COLORS.edge,
        'curve-style': 'bezier',
        'target-arrow-color': COLORS.edge,
        'target-arrow-shape': 'triangle',
        'arrow-scale': 0.8
      }
    }
  ];

  if (isLoading) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Loading graph data...</div>;
  }

  if (error) {
    return <div style={{ padding: '20px', color: 'red', fontFamily: 'Consolas' }}>{error}</div>;
  }

  if (!graphData) {
    return <div style={{ padding: '20px', fontFamily: 'Consolas' }}>Select a graph to visualize</div>;
  }

  return (
    <div style={{ height: 'calc(100vh - 120px)', width: '100%' }}>
      <CytoscapeComponent
        elements={graphData}
        layout={layout}
        stylesheet={stylesheet}
        cy={(cy) => { cyRef.current = cy; }}
        style={{ width: '100%', height: '100%' }}
      />
    </div>
  );
};

export default NetworkGraph;
